import React, { useEffect, useState } from 'react';
import api from '../services';
import { Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const Dashboard = () => {
  const [stats, setStats] = useState({});
  const [chartData, setChartData] = useState({
    labels: ['Appointments', 'Patients'],
    datasets: [
      {
        label: 'Counts',
        data: [0, 0],
        backgroundColor: ['#36A2EB', '#FF6384']
      }
    ]
  }); // default safe data

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const res = await api.get('/doctor/stats');
        const data = res.data || {};
        setStats(data);

        setChartData({
          labels: ['Appointments', 'Patients'],
          datasets: [
            {
              label: 'Counts',
              data: [
                data.appointments != null ? data.appointments : 0,
                data.patients != null ? data.patients : 0
              ],
              backgroundColor: ['#36A2EB', '#FF6384']
            }
          ]
        });
      } catch (error) {
        console.error('Error fetching stats:', error);
        setStats({ appointments: 0, patients: 0 });
        setChartData({
          labels: ['Appointments', 'Patients'],
          datasets: [
            {
              label: 'Counts',
              data: [0, 0],
              backgroundColor: ['#36A2EB', '#FF6384']
            }
          ]
        });
      }
    };

    fetchStats();
  }, []);

  return (
    <div style={styles.page}>
      <h2 style={styles.heading}>Doctor Dashboard</h2>

      {/* Chart Card */}
      <div style={styles.card}>
        <Bar
          data={chartData}
          options={{
            responsive: true,
            plugins: {
              legend: { position: 'top' },
              title: { display: true, text: 'Appointments vs Patients' }
            }
          }}
        />
      </div>

      {/* Stats Table Card */}
      <div style={styles.card}>
        {stats && Object.keys(stats).length > 0 ? (
          <table style={styles.table}>
            <thead>
              <tr>
                <th style={styles.th}>Metric</th>
                <th style={styles.th}>Value</th>
              </tr>
            </thead>
            <tbody>
              {Object.entries(stats).map(([k, v]) => (
                <tr key={k}>
                  <td style={styles.td}>{k}</td>
                  <td style={styles.td}>{v}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p style={{ textAlign: 'center' }}>No statistics available.</p>
        )}
      </div>
    </div>
  );
};

const styles = {
  page: {
    padding: 20,
    minHeight: '100vh',
    background: '#f4f7fa',
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif"
  },
  heading: {
    color: '#0077b6',
    marginBottom: 20,
    fontSize: '2rem',
    textAlign: 'center'
  },
  card: {
    background: '#fff',
    padding: 20,
    borderRadius: 10,
    marginBottom: 20,
    boxShadow: '0 4px 12px rgba(0,0,0,0.05)'
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
    marginTop: 10
  },
  th: {
    padding: 12,
    background: '#0077b6',
    color: '#fff',
    textAlign: 'left',
    borderBottom: '1px solid #e0e0e0'
  },
  td: {
    padding: 12,
    borderBottom: '1px solid #e0e0e0'
  }
};

export default Dashboard;
