import React from "react";
import { Link, useLocation } from "react-router-dom";
import { Home, User, Calendar, FileText, LogOut, BarChart2 } from "lucide-react";

const Sidebar = () => {
  const location = useLocation();

  const menuItems = [
    { name: "Home", icon: <Home size={20} />, path: "/" },
    { name: "Patients", icon: <User size={20} />, path: "/patients" },
    { name: "Appointments", icon: <Calendar size={20} />, path: "/appointments" },
    { name: "Prescriptions", icon: <FileText size={20} />, path: "/prescriptions" },
    { name: "Reports", icon: <BarChart2 size={20} />, path: "/reports" },
  ];

  return (
    <div style={{
      width: "250px",
      minHeight: "100vh",
      display: "flex",
      flexDirection: "column",
      justifyContent: "space-between",
      backgroundColor: "#1f2937",
      color: "#fff",
      position: "fixed",
      left: 0,
      top: 0,
      padding: "1rem 0"
    }}>
      <div>
        <h2 style={{ textAlign: "center", marginBottom: "2rem", fontSize: "1.5rem" }}>HMS Dashboard</h2>
        <ul style={{ listStyle: "none", padding: 0 }}>
          {menuItems.map((item, index) => (
            <li key={index}>
              <Link
                to={item.path}
                style={{
                  display: "flex",
                  alignItems: "center",
                  gap: "0.75rem",
                  padding: "0.75rem 1rem",
                  textDecoration: "none",
                  color: location.pathname === item.path ? "#1f2937" : "#fff",
                  backgroundColor: location.pathname === item.path ? "#fff" : "transparent",
                  borderRadius: "6px",
                  margin: "0.25rem 0",
                  transition: "all 0.2s ease",
                }}
              >
                {item.icon} {item.name}
              </Link>
            </li>
          ))}
        </ul>
      </div>

      {/* Logout fixed at bottom */}
      <div style={{ padding: "0 1rem" }}>
        <Link
          to="/logout"
          style={{
            display: "flex",
            alignItems: "center",
            gap: "0.75rem",
            padding: "0.75rem 1rem",
            textDecoration: "none",
            color: "#fff",
            backgroundColor: "#d32f2f",
            borderRadius: "6px",
            justifyContent: "center",
          }}
        >
          <LogOut size={20} /> Logout
        </Link>
      </div>
    </div>
  );
};

export default Sidebar;
