import React from "react";
import { Link, useNavigate } from "react-router-dom";

const Sidebar = ({ role }) => {
  const navigate = useNavigate();

  const links = {
    admin: [
      { to: "/admin/", label: "Dashboard" },
      { to: "/admin/patients", label: "Patients" },
      { to: "/admin/doctors", label: "Doctors" },
      { to: "/admin/appointments", label: "Appointments" },
      { to: "/admin/billing", label: "Billing" },
      { to: "/admin/reports", label: "Reports" },
      { to: "/admin/notifications", label: "Notifications" },
    ],
    patient: [
      { to: "/patient/", label: "Dashboard" },
      { to: "/patient/add-appointment", label: "Add Appointment" },
      { to: "/patient/appointments", label: "Appointments" },
      { to: "/patient/prescriptions", label: "Prescriptions" },
      { to: "/patient/records", label: "Records" },
      { to: "/patient/billing", label: "Billing" },
      { to: "/patient/reconsult", label: "Reconsult" },
      { to: "/patient/profile", label: "Profile" },
    ],
    doctor: [
      { to: "/doctor/", label: "Dashboard" },
      { to: "/doctor/appointments", label: "Appointments" },
      { to: "/doctor/patients", label: "Patients" },
      { to: "/doctor/availability", label: "Availability" },
    ],
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("role");
    navigate("/login");
  };

  return (
    <div style={styles.sidebar}>
      <h2 style={styles.title}>{role.charAt(0).toUpperCase() + role.slice(1)} Panel</h2>
      <ul style={styles.linkList}>
        {links[role].map((link, index) => (
          <li key={index} style={styles.linkItem}>
            <Link to={link.to} style={styles.link}>
              {link.label}
            </Link>
          </li>
        ))}
      </ul>
      <button onClick={handleLogout} style={styles.logoutBtn}>
        Logout
      </button>
    </div>
  );
};

const styles = {
  sidebar: {
    width: "250px",
    minHeight: "100vh",
    backgroundColor: "#1f2937", // dark gray
    color: "#fff",
    padding: "20px",
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-between",
    position: "fixed",
  },
  title: {
    fontSize: "1.5rem",
    marginBottom: "30px",
    textAlign: "center",
    color: "#f9fafb",
  },
  linkList: {
    listStyle: "none",
    padding: 0,
    flexGrow: 1,
  },
  linkItem: {
    marginBottom: "15px",
  },
  link: {
    color: "#e5e7eb",
    textDecoration: "none",
    fontSize: "1.1rem",
    transition: "0.2s",
  },
  logoutBtn: {
    backgroundColor: "#ef4444", // red
    border: "none",
    padding: "10px",
    borderRadius: "6px",
    color: "#fff",
    fontSize: "1rem",
    cursor: "pointer",
    transition: "0.2s",
  },
};

// Hover effects (inline style workaround)
const addHoverEffect = () => {
  const styleSheet = document.styleSheets[0];
  styleSheet.insertRule(
    `.sidebar a:hover { color: #60a5fa; }`,
    styleSheet.cssRules.length
  );
  styleSheet.insertRule(
    `.sidebar button:hover { background-color: #dc2626; }`,
    styleSheet.cssRules.length
  );
};

addHoverEffect();

export default Sidebar;
